#pragma config(Sensor, S1,     banka,          sensorEV3_Ultrasonic)
#pragma config(Sensor, S2,     senter,         sensorEV3_Color)
#pragma config(Sensor, S3,     left2,          sensorEV3_Color)
#pragma config(Sensor, S4,     right2,         sensorEV3_Color)
#pragma config(Motor,  motorA,          right,         tmotorEV3_Large, openLoop, driveRight, encoder)
#pragma config(Motor,  motorB,          left,          tmotorEV3_Large, openLoop, driveLeft, encoder)
#pragma config(Motor,  motorD,          klehni,        tmotorEV3_Medium, openLoop, encoder)
