task main()
{
	int i=getColorReflected(senter);
	while(true)
	{
		//cenr datchikport2
		//chernij 9
		//belij 75
		if(i>33)
		{
			moveMotorTarget(left, 45, 25);
			moveMotorTarget(right, 20, 25);
		}
		else
		{
			moveMotorTarget(left, 20, 25);
			moveMotorTarget(right, 45, 25);
		}
	}
}
